    private void btnSomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSomaActionPerformed
        int idade = Integer.parseInt(txtN2.getText()); 
        int dependentes = Integer.parseInt(txtN3.getText());
        double salario = Double.parseDouble(txtN1.getText().replaceAll(",","."));
        double base, desconto_idoso, desconto_inss = 0, inss, liquido, irpf;
        double imp_renda = 0;
        
        
            if (salario==0){
               
               lblSoma.setText("Valor do Sal√°rio deve ser maior que Zero.");         
            }
            
            System.out.println("");
            
            if (salario <= 1904.04){
                lblSoma.setText("Voc√™ est√° Isento de Imposto de Renda em 2020.");
            }
            System.out.println("");
            
            if (idade >=65){
                salario = salario - 1903.98;
            }
            System.out.println("");
            
            if (salario>= 1904.04 && salario<= 2822.90){
                inss = (salario*(9/100));
                desconto_inss = salario - inss;
            }
            
            else if (salario>= 2822.91 && salario<= 5645.80){
                inss = (salario*(11/100));
                desconto_inss = salario - inss;
            }
            
            else if (salario>= 5645.81){
                inss = (salario*(11/100));
                desconto_inss = salario - inss;
            }
            
            System.out.println("");
            
            if (dependentes==0){
                liquido = desconto_inss;
            }
            
            else{
            liquido = desconto_inss - (dependentes*189.59);
            }
            
            System.out.println("");
            
            if (liquido>= 1903.99 && liquido<= 2826.65){
			irpf = liquido * 7.5/100;
		 	imp_renda = irpf - 142.80;
	}		
			
            else if (liquido>= 2826.66 && liquido<= 3751.05){
			irpf = liquido * 15/100;
			 imp_renda = irpf - 354.80;
	}	
	
            else if (liquido>= 3751.06 && liquido<= 4664.68){
		irpf = liquido * 22.5/100;
		imp_renda = irpf - 636.13;
	}	
	
		else if (liquido> 4664.68){
		irpf = liquido * 27.5/100;
		imp_renda = irpf - 869.36;
	}	
            
                
        lblSoma.setText("R$: " + Double.toString(imp_renda).format("%.2f", imp_renda));
        
    }//GEN-LAST:event_btnSomaActionPerformed

